import {DropdownDataNumber} from '../../types/dropdownData';
import {parseDate} from '../../utils/dateUtils';
import {TableHead, Typography, Theme} from 'client-library';
import {numberToPriceString} from '../../utils/stringUtils';
import {ContractStatus, Status} from './styles';

export const dashboardContractsTableHeads: TableHead[] = [
  {
    title: 'Dana do isteka',
    accessor: 'days_until_expiry',
    type: 'custom',
    renderContents: (days_until_expiry: number) => {
      const color =
        days_until_expiry <= 30
          ? Theme.palette.error700
          : days_until_expiry > 0 && days_until_expiry < 30
          ? Theme.palette.warning500
          : Theme.palette.warning100;
      return (
        <ContractStatus>
          <Status color={color} />
          <Typography variant="bodySmall" content={days_until_expiry} />
        </ContractStatus>
      );
    },
  },
  {
    title: 'Datum završetka',
    accessor: 'date_of_expiry',
    type: 'custom',
    renderContents: (date_of_expiry: string) => {
      return <Typography variant="bodySmall" content={date_of_expiry ? parseDate(date_of_expiry) : ''} />;
    },
  },
  {
    title: 'Dobavljač',
    accessor: 'supplier',
    type: 'custom',
    renderContents: (supplier: DropdownDataNumber) => {
      return <Typography variant="bodySmall" content={supplier.title} />;
    },
  },
  {
    title: 'Ukupno',
    accessor: 'gross_value',
    type: 'custom',
    renderContents: gross_value => {
      return <Typography variant="bodySmall" content={numberToPriceString(gross_value)} />;
    },
  },
  {title: '', accessor: 'TABLE_ACTIONS', type: 'tableActions'},
];

import React, {useMemo, useState} from 'react';
import {Button, Table, Theme, TrashIcon, Typography, EditIconTwo} from 'client-library';
import {NotificationsModal} from '../../shared/notifications/notificationsModal';
import ScreenWrapper from '../../shared/screenWrapper';
import {ScreenProps} from '../../types/screen-props';
import {tableHeads} from './constants';
import {ButtonWrapper, Container, CustomDivider, MainTitle, TableHeader} from './styles';
import useProcurementContracts from '../../services/graphql/procurementContractsOverview/hooks/useProcurementContracts';
import {ProcurementContract} from '../../types/graphql/procurementContractsTypes';
import {ProcurementContractsFilters} from './filters/procurementContractsFilters';
import useGetSuppliers from '../../services/graphql/suppliers/hooks/useGetSuppliers';
import {ProcurementContractModal} from '../../components/procurementContractModal/procurementContractModal';
import useContractDelete from '../../services/graphql/procurementContractsOverview/hooks/useContractDelete';
import {UserRole} from '../../constants';

export const ProcurementContractsMainPage: React.FC<ScreenProps> = ({context}) => {
  const [showModal, setShowModal] = useState(false);

  const role = context?.contextMain?.role_id;

  const [selectedSupplier, setSelectedSupplier] = useState(0);
  const [searchQuery, setSearchQuery] = useState('');

  const {
    data: tableData,
    refetchData,
    loading,
  } = useProcurementContracts({
    id: undefined,
    procurement_id: undefined,
    supplier_id: selectedSupplier,
  });

  const filteredTableData = tableData?.filter(item => {
    const searchString = searchQuery.toLowerCase();
    const supplier = item?.supplier.title.toLowerCase();
    const serialNumber = item?.serial_number.toLowerCase();
    const procurementTitle = item?.public_procurement?.title?.toLowerCase();

    return (
      supplier.includes(searchString) || serialNumber.includes(searchString) || procurementTitle.includes(searchString)
    );
  });

  const {data: suppliers} = useGetSuppliers({id: 0, search: ''});

  const [selectedItemId, setSelectedItemId] = useState(0);
  const [showDeleteModal, setShowDeleteModal] = useState(false);

  const {mutate: deleteContract} = useContractDelete();

  const selectedItem = useMemo(() => {
    return tableData?.find((item: ProcurementContract) => item.id === selectedItemId);
  }, [tableData, selectedItemId]);

  const handleAdd = () => {
    setShowModal(true);
  };

  const closeModal = () => {
    setShowModal(false);
    setSelectedItemId(0);
  };

  const handleDeleteIconClick = (id: number) => {
    setSelectedItemId(id);
    setShowDeleteModal(true);
  };

  const handleCloseDeleteModal = () => {
    setShowDeleteModal(false);
    setSelectedItemId(0);
  };

  const handleDelete = () => {
    if (showDeleteModal) {
      deleteContract(
        selectedItemId,
        () => {
          setShowDeleteModal(false);
          refetchData();
          context.alert.success('Uspješno obrisano');
        },
        () => {
          setShowDeleteModal(false);
          context.alert.error('Došlo je do greške pri brisanju');
        },
      );
    }
    setSelectedItemId(0);
  };

  return (
    <ScreenWrapper>
      <Container>
        <MainTitle content="LISTA SVIH UGOVORA" variant="bodyMedium" />
        <CustomDivider />
        <TableHeader>
          <ProcurementContractsFilters
            suppliers={suppliers || []}
            setFilters={({supplier_id}) => {
              setSelectedSupplier(supplier_id);
            }}
            searchQuery={searchQuery}
            setSearchQuery={setSearchQuery}
            context={context}
          />

          {role !== UserRole.MANAGER_OJ && (
            <ButtonWrapper>
              <Button
                variant="secondary"
                content={<Typography variant="bodyMedium" content="Novi ugovor" />}
                onClick={handleAdd}
              />
            </ButtonWrapper>
          )}
        </TableHeader>

        <div>
          <Table
            tableHeads={tableHeads}
            data={filteredTableData || []}
            isLoading={loading}
            onRowClick={row => {
              context.navigation.navigate(`/procurements/contracts/${row.id.toString()}/signed`);
              context.breadcrumbs.add({
                name: `Detalji zaključenog ugovora ${row?.serial_number} `,
                to: `/procurements/contracts/${row.id.toString()}/signed`,
              });
            }}
            tableActions={
              role === UserRole.MANAGER_OJ
                ? undefined
                : [
                    {
                      name: 'edit',
                      onClick: (item: any) =>
                        context.navigation.navigate(`/procurements/contracts/${item.id.toString()}`),
                      icon: <EditIconTwo stroke={Theme?.palette?.gray800} />,
                    },
                    {
                      name: 'delete',
                      onClick: (item: ProcurementContract) => handleDeleteIconClick(item.id),
                      icon: <TrashIcon stroke={Theme?.palette?.gray800} />,
                    },
                  ]
            }
          />
        </div>
        {showModal && (
          <ProcurementContractModal
            alert={context.alert}
            fetch={refetchData}
            open={showModal}
            onClose={closeModal}
            selectedItem={selectedItem}
            navigate={context.navigation.navigate}
            context={context}
          />
        )}
        <NotificationsModal
          open={!!showDeleteModal}
          onClose={handleCloseDeleteModal}
          handleLeftButtomClick={handleDelete}
          subTitle={'Ovaj fajl ce biti trajno izbrisan iz sistema'}
        />
      </Container>
    </ScreenWrapper>
  );
};

import {Button, MicroserviceProps, TableHead, Typography} from 'client-library';
import React, {useEffect, useMemo, useState} from 'react';
import useProcurementOrganizationUnitArticleInsert from '../../services/graphql/organizationUnitPublicProcurements/hooks/usePublicProcurementOrganizationUnitArticleInsert';
import useGetProcurementPlanItemLimits from '../../services/graphql/procurementPlanItemLimits/hooks/useGetProcurementPlanItemLimit';
import ScreenWrapper from '../../shared/screenWrapper';
import {CustomDivider, Filters, Header, MainTitle, SectionBox, SubTitle, TableContainer} from '../../shared/styles';
import {AmountInput, Column, FormControls, FormFooter, Plan, Price} from './styles';
import usePublicProcurementPlanDetails from '../../services/graphql/plans/hooks/useGetPlanDetails';
import usePublicProcurementGetDetails from '../../services/graphql/procurements/hooks/useProcurementDetails';
import useGetPublicProcurementOUArticles from '../../services/graphql/plansRequests/hooks/useGetProcurementOUArticles';

interface ProcurementDetailsPageProps {
  context: MicroserviceProps;
}

export const ProcurementDetailsManager: React.FC<ProcurementDetailsPageProps> = ({context}) => {
  const url = context.navigation.location.pathname;
  const planID = url.split('/').at(-3);
  const organizationUnitId = context?.contextMain?.organization_unit?.id;

  const procurementID = url.split('/').at(-1);
  let limit = 0;
  const pathname = url.substring(0, url.lastIndexOf('/', url.lastIndexOf('/') - 1));
  const {data: procurementPlanLimits} = useGetProcurementPlanItemLimits(procurementID);
  const {planDetails} = usePublicProcurementPlanDetails(planID);
  const [requestSuccessCount, setRequestSuccessCount] = useState<number>(0);
  const [requestErrorCount, setRequestErrorCount] = useState<number>(0);

  const {mutate: insertOrganizationUnitArticle, loading: isLoadingInsertOUArticleMutate} =
    useProcurementOrganizationUnitArticleInsert();
  const [filteredArticles, setFilteredArticles] = useState<any[]>([]);

  const handleInputChange = (event: React.ChangeEvent<HTMLInputElement>, row: any) => {
    const {value} = event.target;
    const updatedArticles = [...filteredArticles];
    const index = updatedArticles.findIndex(item => item.id === row.id);

    if (index !== -1) {
      const updatedItem = {...updatedArticles[index], amount: value === '' ? null : +value};
      updatedArticles[index] = updatedItem;
      setFilteredArticles(updatedArticles);
    }
  };

  const {articles: filledArticles, refetch} = useGetPublicProcurementOUArticles(+procurementID, organizationUnitId);

  const {publicProcurement, loading: isLoadingOUProcurementDetails} = usePublicProcurementGetDetails(procurementID);

  const procurement = useMemo(() => {
    if (publicProcurement) {
      return {
        ...publicProcurement,
        articles: publicProcurement?.articles.map(article => {
          return {
            ...article,
            amount: filledArticles?.find(item => item?.public_procurement_article.id === article?.id)?.amount,
          };
        }),
      };
    }
  }, [publicProcurement, filledArticles]);

  const tableHeads: TableHead[] = [
    {
      title: 'Opis predmeta nabavke',
      accessor: 'title',
      type: 'text',
    },
    {
      title: 'Bitne karakteristike',
      accessor: 'description',
      type: 'text',
    },
    {
      title: 'Vrijednost neto',
      accessor: 'net_price',
      type: 'custom',
      renderContents: (net_price: any, row: any) => {
        const netPrice = Number(net_price) * (row?.amount || 1);
        return (
          <Typography
            content={`${netPrice.toLocaleString('sr-RS', {
              minimumFractionDigits: 2,
              maximumFractionDigits: 2,
            })} €`}
            variant="bodySmall"
          />
        );
      },
    },
    {
      title: 'PDV',
      accessor: '',
      type: 'custom',
      renderContents: (_: any, row: any) => {
        const pdvValue = ((Number(row?.net_price) * Number(row?.vat_percentage)) / 100) * (row?.amount || 1);
        return (
          <Typography
            content={`${Number(pdvValue).toLocaleString('sr-RS', {
              minimumFractionDigits: 2,
              maximumFractionDigits: 2,
            })} €`}
            variant="bodySmall"
          />
        );
      },
    },
    {
      title: 'Ukupno',
      accessor: 'total',
      type: 'custom',
      renderContents: (_, row: any) => {
        const pdvValue = (Number(row?.net_price) * Number(row?.vat_percentage)) / 100;
        const total = Number(row?.net_price) + Number(pdvValue);

        const calculateTotal = total * (row?.amount || 1);

        return (
          <Typography
            content={`${calculateTotal.toLocaleString('sr-RS', {
              minimumFractionDigits: 2,
              maximumFractionDigits: 2,
            })} €`}
            variant="bodySmall"
          />
        );
      },
    },
    {
      title: 'Količina',
      accessor: 'amount',
      type: 'custom',
      renderContents: (_, row) => {
        return (
          <AmountInput
            type="number"
            value={row.amount}
            onChange={event => handleInputChange(event, row)}
            disabled={planDetails?.status === 'Odobren' || planDetails?.status === 'Objavljen'}
          />
        );
      },
    },
  ];

  const totalNet =
    filteredArticles.reduce((sum: number, article: any) => sum + parseFloat(article?.net_price) * article?.amount, 0) ||
    0;

  const totalPrice =
    filteredArticles.reduce((sum: number, article: any) => {
      const pdvValue = (Number(article?.net_price) * Number(article?.vat_percentage)) / 100;
      const total = (Number(article?.net_price) + Number(pdvValue)) * article.amount;
      return sum + total;
    }, 0) || 0;

  const desiredItem = procurementPlanLimits?.find(item => {
    return item?.organization_unit?.id === organizationUnitId;
  });

  limit = parseFloat(desiredItem?.limit || '0');

  const handleSave = async () => {
    if (limit !== 0 && totalPrice > limit) {
      context.alert.error('Prekoračili ste limit.');
      return;
    }

    for (const item of filteredArticles) {
      const insertItem = {
        id: filledArticles?.find(article => article?.public_procurement_article.id === item?.id)?.id || undefined,
        public_procurement_article_id: item.id,
        organization_unit_id: organizationUnitId,
        status:
          filledArticles?.find(article => article?.public_procurement_article.id === item?.id)?.status || 'in_progress',
        is_rejected:
          filledArticles?.find(article => article?.public_procurement_article.id === item?.id)?.is_rejected || false,
        rejected_description:
          filledArticles?.find(article => article?.public_procurement_article.id === item?.id)?.rejected_description ||
          '',
        amount: filledArticles?.find(article => article?.public_procurement_article.id === item?.id)?.amount || 0,
      };

      await insertOrganizationUnitArticle(
        insertItem,
        () => {
          setRequestSuccessCount(prevCount => prevCount + 1);
          totalPrice > limit || requestErrorCount > 0 ? '' : context.navigation.navigate(pathname);
          setRequestErrorCount(0);
          context.navigation.navigate(pathname);
          context.breadcrumbs.remove();
        },
        () => {
          context.alert.error('Nije uspješno sačuvano');
          setRequestErrorCount(prevCount => prevCount + 1);
        },
      );
      refetch();
    }
  };

  useEffect(() => {
    if (requestSuccessCount === filteredArticles?.length && requestSuccessCount > 0 && requestErrorCount === 0) {
      context.alert.success('Uspješno sačuvano');
      setRequestSuccessCount(0);
    }
  }, [requestSuccessCount, requestErrorCount, filteredArticles]);

  useEffect(() => {
    if (procurement) {
      setFilteredArticles(procurement?.articles);
    }
  }, [procurement]);

  return (
    <ScreenWrapper>
      <SectionBox>
        <MainTitle
          variant="bodyMedium"
          content={`NABAVKA BROJ. ${procurement?.title || ''} / KONTO: ${procurement?.budget_indent?.title || ''}`}
          style={{marginBottom: 0}}
        />
        <CustomDivider />
        <Header>
          <Filters>
            <Column>
              <SubTitle variant="bodySmall" content="UKUPNA NETO VRIJEDNOST NABAVKE:" />
              <Price
                variant="bodySmall"
                content={`€ ${totalNet.toLocaleString('sr-RS', {
                  minimumFractionDigits: 2,
                  maximumFractionDigits: 2,
                })}`}
              />
            </Column>
            <Column>
              <SubTitle variant="bodySmall" content="UKUPNA BRUTO VRIJEDNOST NABAVKE:" />
              <Price
                variant="bodySmall"
                content={`€ ${totalPrice.toLocaleString('sr-RS', {
                  minimumFractionDigits: 2,
                  maximumFractionDigits: 2,
                })}`}
              />
            </Column>
          </Filters>
        </Header>

        <SubTitle variant="bodySmall" content="LIMIT:" />
        <Price
          variant="bodySmall"
          content={`€ ${
            limit
              ? limit.toLocaleString('sr-RS', {
                  minimumFractionDigits: 2,
                  maximumFractionDigits: 2,
                })
              : ''
          }`}
        />
        <Plan>
          <Typography
            content={procurement?.plan?.id === 1 ? 'PREDBUDŽETSKO' : 'POSTBUDŽETSKO'}
            variant="bodyMedium"
            style={{fontWeight: 600}}
          />
        </Plan>
        <TableContainer
          tableHeads={tableHeads}
          data={filteredArticles || []}
          isLoading={isLoadingOUProcurementDetails}
        />
      </SectionBox>

      <FormFooter>
        <FormControls>
          <>
            <Button
              content="Nazad"
              variant="secondary"
              onClick={() => {
                context.navigation.navigate(pathname);
                context.breadcrumbs.remove();
              }}
            />

            {planDetails?.status !== 'Odobren' && (
              <Button
                content="Sačuvaj"
                variant="primary"
                onClick={() => {
                  handleSave();
                }}
                isLoading={isLoadingInsertOUArticleMutate}
              />
            )}
          </>
        </FormControls>
      </FormFooter>
    </ScreenWrapper>
  );
};

import {Tab} from '@oykos-development/devkit-react-ts-styled-components';
import {Button} from 'client-library';
import React, {useEffect, useState} from 'react';
import {RejectedProcurementModal} from '../../components/rejectedProcurementModal/rejectedProcurementModal';
import {UserPermission, UserRole, checkPermission} from '../../constants';
import usePublicProcurementPlanDetails from '../../services/graphql/plans/hooks/useGetPlanDetails';
import useInsertPublicProcurementPlan from '../../services/graphql/plans/hooks/useInsertPublicProcurementPlan';
import useSendProcurementOnRevision from '../../services/graphql/plans/hooks/useSendProcurementOnRevision';
import {NotificationsModal} from '../../shared/notifications/notificationsModal';
import ScreenWrapper from '../../shared/screenWrapper';
import {CustomDivider, MainTitle, SectionBox} from '../../shared/styles';
import {parseDateForBackend, stringToDate} from '../../utils/dateUtils';
import {RequestsPage} from './requests';
import {FormControls, FormFooter, MessageBox, StyledTabs, TitleTabsWrapper} from './styles';
import {ProcurementsPlanPageProps} from './types';
import {PlanCloseModal} from '../../components/planCloseModal/planCloseModal';
import {PlanDetailsTab} from './planDetailsTab';
import {ProcurementStatus} from '../../types/graphql/publicProcurementPlanItemDetailsTypes';
import useAppContext from '../../context/useAppContext';
import useGetPlanPDFUrl from '../../services/graphql/planPDF/useGetPlanPDFUrl';
import PlanPDFDocument from './planPDF';
import {usePDF} from '@react-pdf/renderer';
import {downloadPDF} from '../../services/constants';

export const ProcurementsPlan: React.FC<ProcurementsPlanPageProps> = () => {
  const {
    contextMain: {role_id},
    navigation,
    alert,
    breadcrumbs,
  } = useAppContext();
  const url = navigation.location.pathname;
  const location = navigation?.location;
  const searchParams = location?.search;

  const planID = +url.split('/').pop();
  const pathname = url.substring(0, url.lastIndexOf('/'));

  const {sendProcurementOnRevision} = useSendProcurementOnRevision();
  const {planDetails, fetch: fetchPlanDetails, loading: isLoadingPlanDetails} = usePublicProcurementPlanDetails(planID);
  const {mutate: insertPlan} = useInsertPublicProcurementPlan();

  enum TabsEnum {
    Overview = 1,
    Requests = 2,
    SimpleProcurement = 3,
  }

  const [activeTab, setActiveTab] = useState<TabsEnum>(
    searchParams ? TabsEnum.Requests : location?.state?.activeTab ? location?.state?.activeTab : TabsEnum.Overview,
  );
  const [isNotificationModalActive, setIsNotificationModalActive] = useState<boolean>(false);
  const [isRejectedModalActive, setIsRejectedModalActive] = useState<boolean>(false);
  const [showPlanCloseModal, setShowPlanCloseModal] = useState<boolean>(false);
  const [dateOfClosing, setDateOfClosing] = useState('');

  const planTabs = [
    {id: TabsEnum.Overview, title: 'Pregled', routeName: 'overview', disabled: false},
    {id: TabsEnum.Requests, title: 'Zahtjevi', routeName: 'requests', disabled: planDetails?.status != 'Poslat'},
    {
      id: TabsEnum.SimpleProcurement,
      title: 'Jednostavne nabavke',
    },
  ];

  const buttonSendEnable = planDetails?.items
    ?.filter(item => item.is_open_procurement === true)
    .every(item => item.status === ProcurementStatus.ProcurementStatusProcessed);

  const onTabChange = (tab: Tab) => {
    setActiveTab(tab.id as number);
  };

  const handleUpdatePlan = () => {
    sendProcurementOnRevision(planDetails?.id, () => {
      navigation.navigate(pathname);
      setIsNotificationModalActive(false);
      setIsRejectedModalActive(false);
    });
  };

  const handleCompletePlan = () => {
    if (!planDetails) return;
    const payload = {
      id: planDetails?.id,
      pre_budget_id: planDetails?.pre_budget_plan?.id || undefined,
      is_pre_budget: planDetails?.is_pre_budget,
      year: planDetails?.year,
      title: planDetails?.title,
      serial_number: planDetails?.serial_number,
      date_of_publishing: parseDateForBackend(new Date(planDetails?.date_of_publishing)),
      date_of_closing: parseDateForBackend(stringToDate(dateOfClosing)),
      file_id: planDetails?.file_id,
    };

    insertPlan(
      payload,
      () => {
        navigation.navigate('/procurements/plans');
        alert.success('Plan uspješno zaključen');
      },
      () => alert.error('Došlo je do greške pri zaključivanju plana'),
    );
  };

  const getTitle = () => {
    switch (activeTab) {
      case TabsEnum.Overview:
        return `PLAN ZA ${planDetails?.year} - ${planDetails?.is_pre_budget ? 'PREDBUDŽETSKO' : 'POSTBUDŽETSKO'}`;
      case TabsEnum.Requests:
        return 'ZAHTJEVI';
      case TabsEnum.SimpleProcurement:
        return `JEDNOSTAVNE NABAVKE ZA PLAN ${planDetails?.year}`;
      default:
        throw new Error(`Tab ${activeTab} does not exist`);
    }
  };

  const [contractPDF, updateInstance] = usePDF({});

  const {pdfData, loading: loadingReport} = useGetPlanPDFUrl({
    plan_id: planID,
  });

  useEffect(() => {
    if (pdfData) {
      updateInstance(<PlanPDFDocument data={pdfData} />);
    }
  }, [pdfData]);

  return (
    <ScreenWrapper>
      <SectionBox>
        <TitleTabsWrapper>
          <MainTitle variant="bodyMedium" content={getTitle()} style={{marginBottom: 0}} />
          {checkPermission(role_id, UserPermission.VIEW_PLANS_REQUESTS) && (
            <StyledTabs tabs={planTabs} activeTab={activeTab} onChange={onTabChange} />
          )}
        </TitleTabsWrapper>
        <CustomDivider style={{marginTop: 0}} />
        {(activeTab === TabsEnum.Overview || activeTab === TabsEnum.SimpleProcurement) && (
          <PlanDetailsTab
            planDetails={planDetails}
            fetchPlanDetails={fetchPlanDetails}
            isLoadingPlanDetails={isLoadingPlanDetails}
            isSimpleProcurement={activeTab === TabsEnum.SimpleProcurement}
          />
        )}
        {activeTab === TabsEnum.Requests && (
          <RequestsPage plan={planDetails} handleDateOfClosing={(date: string) => setDateOfClosing(date)} />
        )}
      </SectionBox>
      {planDetails?.status === 'Obradi' &&
        planDetails.rejected_description !== null &&
        checkPermission(role_id, UserPermission.VIEW_REJECTED_PROCUREMENT_COMMENT) && (
          <MessageBox>{`Razlog odbijanja: ${planDetails.rejected_description}`}</MessageBox>
        )}

      <FormFooter>
        <FormControls>
          <>
            {role_id !== UserRole.MANAGER_OJ && planDetails?.status === 'Objavljen' && (
              <Button
                content="Generiši izvještaj"
                variant="secondary"
                onClick={() => downloadPDF(contractPDF.blob, pdfData)}
                isLoading={loadingReport}
              />
            )}
            <Button
              content="Nazad"
              variant="secondary"
              onClick={() => {
                navigation.navigate(pathname);
                breadcrumbs.remove();
              }}
            />
            {checkPermission(role_id, UserPermission.SEND_PROCUREMENTS) &&
              planDetails?.rejected_description === null && (
                <Button
                  content="Pošalji"
                  variant="primary"
                  onClick={() => setIsNotificationModalActive(true)}
                  disabled={!buttonSendEnable || planDetails?.status === 'Na čekanju'}
                />
              )}

            {checkPermission(role_id, UserPermission.SEND_PROCUREMENTS) &&
              planDetails?.status === 'Obradi' &&
              planDetails.rejected_description !== null && (
                <Button
                  content="Pošalji"
                  variant="primary"
                  onClick={() => setIsRejectedModalActive(true)}
                  disabled={!buttonSendEnable}
                />
              )}

            <NotificationsModal
              open={!!isNotificationModalActive}
              onClose={() => setIsNotificationModalActive(false)}
              handleLeftButtomClick={handleUpdatePlan}
              subTitle="Naknadne izmjene neće biti moguće."
            />

            <RejectedProcurementModal
              open={!!isRejectedModalActive}
              onClose={() => setIsRejectedModalActive(false)}
              handleRightButtonClick={handleUpdatePlan}
            />
          </>
          {activeTab === 2 && (
            <Button
              content={planDetails?.is_pre_budget ? 'Zaključi' : 'Objavi'}
              variant="primary"
              onClick={() => (planDetails?.is_pre_budget ? setShowPlanCloseModal(true) : handleCompletePlan())}
              disabled={!dateOfClosing}
            />
          )}
        </FormControls>
      </FormFooter>

      {showPlanCloseModal && (
        <PlanCloseModal
          open={!!showPlanCloseModal}
          onClose={() => setShowPlanCloseModal(false)}
          handleRightButtonClick={handleCompletePlan}
        />
      )}
    </ScreenWrapper>
  );
};

